svg.script:
var svgDocument;
var parent_content;

function init(evt) {
  svgDocument = evt.target.ownerDocument;
  parent_content = (parent.content)?(parent.content):(parent);
}
function mouseMove(evt) {
  var obj = svgDocument.obj;
  var id = svgDocument.object_id;
  var x = obj.getBoundingClientRect().left + evt.clientX;
  var y = obj.getBoundingClientRect().top + evt.clientY;
  parent_content.show_position("svg",x,y);
}
